    throw new Error("El tipo de autorizacin debe ser Basic");
  }

  // username:contrasea base 64
  const [username, password] = Buffer.from(credentials, "base64")
    .toString()
    .split(":");

  return { username, password };
};

export const getToken = (req) => {
  const { authorization } = req.headers;

  if (!authorization) {
    throw new Error("No se proporcion ningn headers de autorizacin");
  }

  // Bearer eyJhbGciOiJIUzI1(...)
  const [type, token] = authorization.split(" ");

  if (type !== "Bearer") {
    throw new Error("El tipo de autorizacin debe ser Bearer");
  }

  return token;
};

